export default {
  data: function data() {
    return {
      task: {},
      taskName: ""
    };
  },
  mounted: function mounted() {
    if (this.$store.state.task.length == 0 || !this.$router.params["id"]) {
      this.$router.push("/task_list");
    } else {
      // paramからタスクのIDを取得
      var task_id = this.$router.params["id"]; // vuexのストアからタスクIDでタスクを取得する

      this.task = this.$store.getters.getTaskFromId(task_id);
    }
  }
};
