import Vue from "vue";
import App from "./App.vue";
import vuetify from "./plugins/vuetify";
import Axios from "axios";
import VueCookies from "vue-cookies";
import store from "./store/store";
import * as VeeValidate from "vee-validate";
import ja from "vee-validate/dist/locale/ja";
import { localize } from "vee-validate";
import router from "./router/router";

Vue.config.productionTip = false;
localize("ja", ja);
Vue.use(VeeValidate, { locale: ja });

Vue.use(VueCookies);

Axios.defaults.baseURL =
  process.env.NODE_ENV === "production"
    ? "http://backend.habituation-quest.tk"
    : "http://localhost:3030";

router.beforeEach((to, from, next) => {
  const accessToken = this.$cookies.get("access-token");
  const client = this.$cookies.get("client");
  const uid = this.$cookies.get("uid");
  if (accessToken && client && uid) {
    this.$store.commit(
      "setRequestHeadersRequiredToMaintainLoginStatus",
      accessToken,
      client,
      uid
    );
    this.$store.commit("setLoggedIn", true);
  }
  // isPublic でない場合(=認証が必要な場合)、かつ、ログインしていない場合
  if (
    to.matched.some((record) => !record.meta.isPublic) &&
    !store.getters.getLoggedIn
  ) {
    // queryはログイン後の遷移などに使用できる
    next({ path: "/login", query: { redirect: to.fullPath } });
    // リロードした時は、クッキーがあればloggeInがtrueになるので、その前にこのif分が実行されるとだめ
    // それまで待つにはどうすればいいか
  } else {
    next();
  }
});

Vue.config.productionTip = false;

new Vue({
  vuetify,
  render: (h) => h(App),
  router: router,
  store,
}).$mount("#app");
