import Vue from "vue";
import Vuex from "vuex";

Vue.use(Vuex);

export default new Vuex.Store({
  state: {
    isLoggedIn: false,
    name: "Mory",
    count: 0,
    tasks: [],
  },
  getters: {
    counter: (state) => state.count * 100,
    getTaskFromId: (state) => (task_id) => {
      console.log(typeof task_id);
      console.log(state.tasks);
      let targetTask = {};
      state.tasks.forEach((task) => {
        console.log(task.id);
        console.log(typeof task.id);
        if (task.id === task_id) {
          console.log(true);
          targetTask = task;
        }
      });
    },
    getTasks(state) {
      return state.tasks;
    },
  },
  mutations: {
    setIsLoggedIn(state, value) {
      state.isLoggedIn = value;
    },
    setTasks(state, value) {
      state.tasks = value;
    },
  },
});
