import Vue from "vue";
import Vuex from "vuex";

Vue.use(Vuex);

export default new Vuex.Store({
  state: {
    isLoggedIn: false,
    name: "Mory",
    count: 0,
    tasks: [],
  },
  getters: {
    counter: (state) => state.count * 100,
    /*
     * tasks配列からidを使用して目的のタスクを見つける関数

     */
    getTaskFromId: (state) => (task_id) => {
      let targetTask = {};
      state.tasks.forEach((task) => {
        if (task.id === task_id) {
          console.log(true);
          targetTask = task;
        }
      });

      return targetTask;
    },
    getTasks(state) {
      return state.tasks;
    },
  },
  mutations: {
    setIsLoggedIn(state, value) {
      state.isLoggedIn = value;
    },
    setTasks(state, value) {
      state.tasks = value;
    },
  },
});
