import Vue from "vue";
import App from "./App.vue";
import vuetify from "./plugins/vuetify";
import Axios from "axios";
import VueRouter from "vue-router";
import VueCookies from "vue-cookies";
import Vuex from "vuex";
import store from "./store/store";
import * as VeeValidate from "vee-validate";
import ja from "vee-validate/dist/locale/ja";
import { localize } from "vee-validate";

Vue.config.productionTip = false;
localize("ja", ja);
Vue.use(VeeValidate, { locale: ja });

Vue.use(Vuex);

Vue.use(VueCookies);

import HomePage from "@/components/HomePage.vue";
import LoginPage from "@/components/LoginPage.vue";
import LogoutPage from "@/components/LogoutPage.vue";
import SignUpPage from "@/components/SignUpPage.vue";
import HelpPage from "@/components/HelpPage.vue";
import AccountPage from "@/components/AccountPage.vue";
import RecordPage from "@/components/RecordPage.vue";
import ShowRecordPage from "@/components/ShowRecordPage.vue";
import ClassChangePage from "@/components/ClassChangePage.vue";
import TaskListPage from "@/components/TaskListPage.vue";
import TaskPage from "@/components/TaskPage.vue";
import TaskCreatePage from "@/components/TaskCreatePage.vue";
import TaskEditPage from "@/components/TaskEditPage.vue";

Axios.defaults.baseURL =
  process.env.NODE_ENV === "production"
    ? "http://backend.habituation-quest.tk"
    : "http://localhost:3030";

Vue.use(VueRouter);

let Auth = {
  loggedIn: false,
  login: function () {
    this.loggedIn = true;
  },
  logout: function () {
    this.loggedIn = false;
  },
};

const routes = [
  { path: "/", component: HomePage },
  { path: "/login", component: LoginPage },
  { path: "/logout", component: LogoutPage, meta: { requiresAuth: true } },
  { path: "/signup", component: SignUpPage },
  { path: "/help", component: HelpPage },
  { path: "/account", component: AccountPage, meta: { requiresAuth: true } },
  { path: "/record", component: RecordPage, meta: { requiresAuth: true } },
  {
    path: "/show_record",
    component: ShowRecordPage,
    meta: { requiresAuth: true },
  },
  {
    path: "/class_change",
    component: ClassChangePage,
    meta: { requiresAuth: true },
  },
  { path: "/task_list", component: TaskListPage, meta: { requiresAuth: true } },
  { path: "/task/:id", component: TaskPage, meta: { requiresAuth: true } },
  {
    path: "/task_create",
    component: TaskCreatePage,
    meta: { requiresAuth: true },
  },
  {
    path: "/task_edit/:id",
    component: TaskEditPage,
    meta: { requiresAuth: true },
  },
];

const router = new VueRouter({
  mode: "history",
  routes,
});

router.beforeEach((to, from, next) => {
  if (to.matched.some((record) => record.meta.requiresAuth)) {
    next({ path: "/login", query: { redirect: to.fullPath } });
  } else {
    next();
  }
});

Vue.config.productionTip = false;

new Vue({
  vuetify,
  render: (h) => h(App),
  router: router,
  store,
}).$mount("#app");
