export default {
  components: {
    AppHeader: AppHeader,
    AppFooter: AppFooter
  },
  created: function created() {
    var accessToken = this.$cookies.get("access-token");
    var client = this.$cookies.get("client");
    var uid = this.$cookies.get("uid");

    if (accessToken && client && uid) {
      this.$store.commit("setRequestHeadersRequiredToMaintainLoginStatus", accessToken, client, uid);
      this.$store.commit("setIsLoggedIn", true);
    }
  }
};
