import Vue from "vue";
import Vuex from "vuex";

Vue.use(Vuex);

export default new Vuex.Store({
  state: {
    isLoggedIn: false,
    name: "Mory",
    count: 0,
    tasks: [],
  },
  getters: {
    counter: (state) => state.count * 100,
    getTask: (taskId) => {},
  },
  mutations: {
    setIsLoggedIn(state, value) {
      state.isLoggedIn = value;
    },
    setTasks(state, value) {
      state.tasks = value;
    },
  },
});
